# Python Challenge No.5

from urllib import urlopen
import pickle
import sys

url = "http://www.pythonchallenge.com/pc/def/banner.p"

result = ""

req = urlopen(url)
data = pickle.load(req)

for pickles in data:
    for p in pickles:
        for i in range(p[1]):
            sys.stdout.write(p[0])

